import React, { useState, useEffect } from 'react';

import CardList from './CardList';
import SearchBox from './SearchBox'
import './App.css';


const App = () => {
  const [robots, setRobots] = useState();
  const [searchfield, setSearchfield] = useState('');

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(users => setRobots(users));
  }, []);

  const onSearchfield = e => {
    setSearchfield(e.target.value);
    filteredRobots();
  };

  const filteredRobots = (sf) => {
    if (!robots) return {};
    if (sf.length === 0) return robots;
    return JSON.parse(JSON.stringify(robots)).filter(robot => {
      robot.name.toLowerCase().includes(sf.toLowerCase())
    });
  };

  return (!robots) ? <h1>Loading</h1> : (
    <div className='tc'>
      <h1 className='f1'>[Robo]-Friends</h1>
      <SearchBox searchChanged={ onSearchfield }></SearchBox>
      <CardList robots={ filteredRobots(searchfield) } />
    </div>
  );
}

export default App;
