import React, { useState, useEffect } from 'react';

import CardList from './CardList';
import SearchBox from './SearchBox'
// import { robots as ROBOTS } from './robots';
import './App.css';


const App = () => {

  const getRobots = () => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(users => setRobots({ robots: users }))
  };

  const [robots, setRobots] = useState(getRobots());
  const [searchfield, setSearchfield] = useState('');

  useEffect(() => {
    console.log('check');
    getRobots();
  }, []);

  const onSearchfield = e => {
    setSearchfield(e.target.value);
    let r = (e.target.value === '') ? getRobots() : filteredRobots();
    setRobots(r);
  };

  const filteredRobots = () => {
    if (!robots) return {};
    return robots.filter(robot => {
      return robot.name.toLowerCase().includes(searchfield.toLowerCase());
    });
  };

  return (!robots) ? <h1>Loading</h1> : (
    <div className='tc'>
      <h1 className='f1'>[Robo]-Friends</h1>
      <SearchBox searchChanged={ onSearchfield }></SearchBox>
      <CardList robots={ robots } />
    </div>
  );
}

export default App;
